enum Event<'a, T> where T: 'static {
    NewEvents(StartCause),
    WindowEvent {
            window_id: WindowId,
            event: WindowEvent,
        },
    DeviceEvent {
            device_id: DeviceId,
            event: DeviceEvent,
        },
    UserEvent(T),
    Suspended,
    Resumed,
    MainEventsCleared,
    RedrawRequested(WindowId),
    RedrawEventsCleared,
    LoopDestroyed,
}

enum StartCause {
    ResumeTimeReached {
            start: Instant,
            requested_resume: Instant,
        },
    WaitCancelled {
            start: Instant,
            requested_resume: Option<Instant>,
        },
    Poll,
    Init,
}

enum WindowEvent<'a> {
    Resized(PhysicalSize<u32>),
    Moved(PhysicalPosition<i32>),
    CloseRequested,
    Destroyed,
    DroppedFile(PathBuf),
    HoveredFile(PathBuf),
    HoveredFileCancelled,
    ReceivedCharacter(char),
    Focused(bool),
    KeyboardInput {
            device_id: DeviceId,
            input: KeyboardInput,
            is_synthetic: bool,
        },
    ModifiersChanged(ModifiersState),
    Ime(Ime),
    CursorMoved {
            device_id: DeviceId,
            position: PhysicalPosition<f64>,
            modifiers: ModifiersState,
        },
    CursorEntered {
            device_id: DeviceId,
        },
    CursorLeft {
            device_id: DeviceId,
        },
    MouseWheel {
            device_id: DeviceId,
            delta: MouseScrollDelta,
            phase: TouchPhase,
            modifiers: ModifiersState,
        },
    MouseInput {
            device_id: DeviceId,
            state: ElementState,
            button: MouseButton,
            modifiers: ModifiersState,
        },
    TouchpadPressure {
            device_id: DeviceId,
            pressure: f32,
            stage: i64,
        },
    AxisMotion {
            device_id: DeviceId,
            axis: AxisId,
            value: f64,
        },
    Touch(Touch),
    ScaleFactorChanged {
            scale_factor: f64,
            new_inner_size: &'a mut PhysicalSize<u32>,
        },
    ThemeChanged(Theme),
    Occluded(bool),
}

enum DeviceEvent {
    Added,
    Removed,
    MouseMotion {
            delta: (f64, f64),
        },
    MouseWheel {
            delta: MouseScrollDelta,
        },
    Motion {
            axis: AxisId,
            value: f64,
        },
    Button {
            button: ButtonId,
            state: ElementState,
        },
    Key(KeyboardInput),
    Text {
            codepoint: char,
        },
}

enum Ime {
    Enabled,
    Preedit(String, Option<(usize, usize)>),
    Commit(String),
    Disabled,
}

enum Force {
    Calibrated {
            force: f64,
            max_possible_force: f64,
            altitude_angle: Option<f64>,
        },
    Normalized(f64),
}

fn run<F>(Self, F) -> ! where F: 'static +
for<'_, '_,
'_> FnMut(Event, &'_ EventLoopWindowTarget<T>, &'_ mut ControlFlow) -> ()

enum BadIcon {
    ByteCountNotDivisibleBy4 {
            byte_count: usize,
        },
    DimensionsVsPixelCount {
            width: u32,
            height: u32,
            width_x_height: usize,
            pixel_count: usize,
        },
    OsError(io::Error),
}
